import React from "react";
import styled from "styled-components";


class LoginFormInput extends React.Component {
  render() {
    return (
      <StyledLoginFormInputGroup>
<label for={this.props.name}>{this.props.title}</label>
<input name={this.props.name} type={this.props.type?this.props.type:'text'}/>
      </StyledLoginFormInputGroup>
    );
  }
}
export default LoginFormInput;

const StyledLoginFormInputGroup = styled.form`
display:flex;
jusitfy-content:flex-start;
align-items:center;
margin:10px 0;
`;
