  const [email,setEmail]=useState('');
  const [emailErrorInfo,setEmailErrorInfo]=useState(false);
  const [password,setPassword]=useState('');
  const [passwordErrorInfo,setPasswordErrorInfo]=useState(false);

  const handleFormSubmit=()=>{

    let isProperForm=true;
    if(!checkIsValidEmail)
    {
      isProperForm=false;
      emailErrorInfo="Adres e-mail jest nieprawidłowy";
    }
    else
    {
      emailErrorInfo="";
    }




  }

    return <LoginForm title="Zaloguj się">
      <LoginFormInput value={email} onChange={setEmail} errorInfo={emailErrorInfo} title="Adres e-mail" type="e-mail" name="e-mail"/>
      <LoginFormInput value={password} onChange={setPassword} errorInfo={passwordErrorInfo} title="Hasło" type="password" name="password"/>
      <LoginFormButton  name="Zaloguj się"/>

      <LoginFormLinks>
      <Link to="/password-reset">Przypomnij hasło.</Link>
      </LoginFormLinks>

    </LoginForm>;
}
export default Login;
